(1515, 190, '_form', '<div class=\"form-regis\">\n                        <div class=\"item-form\">[text* Name class:txt_field placeholder \"Họ và tên\"]</div>\n                        <div class=\"item-form\">[text* Phone class:txt_field placeholder \"Điện thoại\"]</div>\n                        <div class=\"item-form\">[text* Email class:txt_field placeholder \"Email\"]</div>\n                        <div class=\"item-form\">\n                            <p>Căn hộ bạn quan tâm </p>\n                            <div class=\"row\">\n                                <div class=\"col-md-4\">\n                                    <div class=\"col-regis\">\n                                        <h3 class=\"text-uppercase\">PHONG SƠN</h3>\n                                        [checkbox phongson use_label_element \"Shophouse\" \"Mini Hotel\" \"Biệt thự\"]\n                                    </div>\n                                </div>\n                                <div class=\"col-md-4\">\n                                    <div class=\"col-regis\">\n                                        <h3 class=\"text-uppercase\">Song Vũ</h3>\n                                        [checkbox SongVu use_label_element \"Biệt thự Đơn lập\" \"Biệt thự Song lập\" \"Shopvilla Đơn lập 2,5 tầng\" \"Shopvilla Đơn lập 3 tầng\" \"Shopvilla Song lập 2,5 tầng\" \"Shopvilla Song lập 3 tầng\" \"Liên kề\"]\n                                    </div>\n                                </div>\n                                <div class=\"col-md-4\">\n                                    <div class=\"col-regis\">\n                                        <h3 class=\"text-uppercase\">Trường Vĩ</h3>\n                                        [checkbox TruongVi use_label_element \"Shophouse\" \"Mini Hotel\" \"Biệt thự\"]\n                                    </div>\n                                </div>\n                            </div>\n                        </div>\n                        <div class=\"item-form\">[text* Content class:txt_field placeholder \"Nội dung\"]</div>\n                        <div class=\"item-form\">[submit class:btn-regis class:text-center \"GỬI THÔNG TIN\"]</div>\n                    </div>'),
